import React, { Component } from 'react';
import * as glob from '../scripts/global';

export class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            base64: null
        }
        while (glob.getHome().length > 0)
            glob.getHome().pop();
        glob.getHome().push(this);
    }

    setStream(img) {
        this.setState({base64 : img});
    }

    interact(e) {
        glob.sendMouse(e.clientX, e.clientY)
    }

    render() {
        return (
            <div>
                {this.state.base64 ? <img src={"data:image/jpg;base64," + this.state.base64} onClick={interact}></img> : <div></div>}
            </div>
        );
    }
}
