var connection;
var home = [];
var offsetRatio = 2;                    //config?

export function getHome() { return home; }

export function init(signalRConnection) {
    connection = signalRConnection;
}









export function setStream(data) {
    home[0].setStream(data);
}

export function sendLeftClick(x, y) {
    connection.invoke("SendLeftClick", x * offsetRatio, y * offsetRatio);
}
