import React, { Component } from 'react';
import $ from 'jquery';
import * as glob from '../scripts/global';

export class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            base64: null,
            leftOffset: 0,
            topOffset: 0,
        }
        while (glob.getHome().length > 0)
            glob.getHome().pop();
        glob.getHome().push(this);

        this.interact = this.interact.bind(this);
    }

    componentDidMount() {
        console.log($('#remoteScreen').offset().left);
        console.log($('#remoteScreen').offset().top);
        this.setState({ leftOffset: $('#remoteScreen').offset().left });
        this.setState({ topOffset: $('#remoteScreen').offset().top });
    }

    setStream(img) {
        this.setState({base64 : img});
    }

    interact(e) {
        glob.sendLeftClick(e.clientX - leftOffset, e.clientY - topOffset);
    }

    render() {
        return (
            <div id="remoteScreen">
                {this.state.base64 ? <img src={"data:image/jpg;base64," + this.state.base64} onClick={this.interact}></img> : <div></div>}
            </div>
        );
    }
}
